import './App.css';
import React,{useState} from "react";
import ReactTooltip from "react-tooltip";
import{
  ComposableMap,
  Geographies,
  Geography,
  Marker,Annotation,
  ZoomableGroup,
} from "react-simple-maps";

const marker=[
  {
    markerOffset: -11,
    name:"Delhi 100,00000",
    coordinbates:[77.1025,28.7041],

  },
  {
    markerOffset: -11,
    name:"Uruguay 100,000",
    coordinbates:[-55.7658,-32.5228],

  },
  {
    markerOffset: -11,
    name:"Peru 100,000",
    coordinbates:[-75.0152,-9.1900],

  },
  {
    markerOffset: -11,
    name:"Paris 500,000",
    coordinbates:[2.3522,48.8566],

  },
  {
    markerOffset: -11,
    name:"nigeria 1000",
    coordinbates:[8.6753,9.0820],

  },
  {
    markerOffset: -11,
    name:"Egypt 60,000",
    coordinbates:[30.8025,-6.8206],

  },
  {
    markerOffset: -11,
    name:"Kenya 10,000",
    coordinbates:[15.9062,-25.0236],

  },
  {
    markerOffset: -11,
    name:"moscow 60,000",
    coordinbates:[110.6173,55.7558],

  },
  {
    markerOffset: -11,
    name:"Tokyo 500,0000",
    coordinbates:[120.6503,35.6762],

  },
  {
    markerOffset: -11,
    name:"Indonesia 50,000",
    coordinbates:[113.9213,-30.7893],

  },
]

const geoUrl="https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";

function App() {
  return (
    <div className="App" style={{
      width:"100%",
      height:"100%",
      display:"flex",
      flexDirection:"column",
      justifyContent:"center",
      alignItems:"center",
    }}>
     <h1>Map UI</h1>
     <div style={{width:"1400px",borderStyle:"double"}}>
       <ComposableMap data-tip="">
         <ZoomableGroup zoom={1}>
           {" "}
           <Geographies geography={geoUrl}>
           {({geographies})=>
           geographies.map((geo)=>(
             <Geography key={geo.rsmKey} geography={geo}/>
           ))
           }
         </Geographies>
         {
           marker.map(({name, coordinbates, markerOffset})=>(
             <Marker key={name} coordinates={coordinbates } lable="afgdnm">
                <circle r={10} fill="#db4dff"></circle>
                <text textAnchor='"middle' y={markerOffset} style={{fontFamily:"system-ui", fill:"#5D5A6D", fontSize:"12px", margin:" "}}>
                  {name}                
                </text>
             </Marker>

           ))

         }
         </ZoomableGroup>

       </ComposableMap>
     </div>
    </div>
  );
}

export default App;
