import './App.css';
import React,{useState} from "react";
import ReactTooltip from "react-tooltip";
import{
  ComposableMap,
  Geographies,
  Geography,
  Marker,Annotation,
  ZoomableGroup,
} from "react-simple-maps";

const marker=[
  {
    markerOffset: -15,
    name:"Sau Paul",
    coordinbates:[-28.7041,77.1025]
  },
]

const geoUrl="https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";

function App() {
  return (
    <div className="App" style={{
      width:"100%",
      height:"100%",
      display:"flex",
      flexDirection:"column",
      justifyContent:"center",
      alignItems:"center",
    }}>
     <h1>Map UI</h1>
     <div style={{width:"1400px",borderStyle:"double"}}>
       <ComposableMap data-tip="">
         <ZoomableGroup zoom={1}>
           {" "}
           <Geographies geography={geoUrl}>
           {({geographies})=>
           geographies.map((geo)=>(
             <Geography key={geo.rsmKey} geography={geo}/>
           ))
           }
         </Geographies>
         {
           marker.map(({name, coordinbates, markerOffset})=>(
             <Marker key={name} coordinates={coordinbates }>
                <circle r={10} fill="#F00" stroke='#fff' strokeWidth={2}/>
                <text textAnchor='"middle' y={markerOffset} style={{fontFamily:"system-ui", fill:"#5D5A6D"}}>
                  {name}                
                </text>
             </Marker>
           ))
         }
         </ZoomableGroup>

       </ComposableMap>
     </div>
    </div>
  );
}

export default App;
